import React from 'react';
import './App.css';

interface Note {
  subject: string;
  text: string;
};

export default function App() {
  const [note, setNote] = React.useState<Note>({ subject: '', text: '' });

  return (
    <div className="container">
      <div className="form">
        <h1 style={{ color: '#93469c' }}> Bloco de notas </h1>
        <input 
          placeholder="Assunto" 
          className="input" 
          type="text" 
          onChange={(e) => setNote({ ...note, subject: e.target.value })}
          value={note.subject}
        />
        <textarea 
          placeholder="Texto" 
          rows={10} 
          className="textArea"
          onChange={(e) => setNote({ ...note, text: e.target.value })}
          value={note.text}
        ></textarea>
        <button className="btn">CRIAR NOTA</button>
      </div>
      <div className="notes">
        <h1 style={{ color: '#93469c' }}> Suas notas </h1>
        <div className="card-notes">
          <div className="card">
            <div className="card-header">
              <label className="color-primary">Ideafix</label>
              <div className="card-delete">&times;</div>
            </div>
            <hr className="border-dashed" />
            <div className="card-body">Empresa</div>
          </div>
          <div className="card">
            <div className="card-header">
              <label className="color-primary">Ideafix</label>
              <div className="card-delete">&times;</div>
            </div>
            <hr className="border-dashed" />
            <div className="card-body">Empresa</div>
          </div>
          <div className="card">
            <div className="card-header">
              <label className="color-primary">Ideafix</label>
              <div className="card-delete">&times;</div>
            </div>
            <hr className="border-dashed" />
            <div className="card-body">Empresa</div>
          </div>
        </div>
      </div>
    </div>
  );
};
