import React from 'react';
import { useAxios } from './hooks/useAxios';
import './App.css';

interface Note {
  subject: string;
  text: string;
};

export default function App() {
  const { axios } = useAxios();
  const [notes, setNotes] = React.useState<Array<Note>>();
  const [note, setNote] = React.useState<Note>({ subject: '', text: '' });

  /**
   * Pega todos os blocos
   */
  const index = async () => (await axios.get('/notas'));

  /**
   * Envia dados para o backend
   */
  const onSubmit = async () => {
    await axios.post('/notas/criar', { note })
      .then((res) => console.log(res))
      .catch((rej) => console.log(rej));
  }

  React.useEffect(() => {
    return () => {
      index().then((res) => {
        setNotes(res.data);
      });
    }
  }, []);

  return (
    <div className="container">
      {JSON.stringify(notes)}
      <div className="form">
        <h1 style={{ color: '#93469c' }}> Bloco de notas </h1>
        <input 
          placeholder="Assunto" 
          className="input" 
          type="text" 
          onChange={(e) => setNote({ ...note, subject: e.target.value })}
          value={note.subject}
        />
        <textarea 
          placeholder="Texto" 
          rows={10} 
          className="textArea"
          onChange={(e) => setNote({ ...note, text: e.target.value })}
          value={note.text}
        ></textarea>
        <button onClick={onSubmit} className="btn">CRIAR NOTA</button>
      </div>
      <div className="notes">
        <h1 style={{ color: '#93469c' }}> Suas notas </h1>
        <div className="card-notes">
          <div className="card">
            <div className="card-header">
              <label className="color-primary">Ideafix</label>
              <div className="card-delete">&times;</div>
            </div>
            <hr className="border-dashed" />
            <div className="card-body">Empresa</div>
          </div>
          <div className="card">
            <div className="card-header">
              <label className="color-primary">Ideafix</label>
              <div className="card-delete">&times;</div>
            </div>
            <hr className="border-dashed" />
            <div className="card-body">Empresa</div>
          </div>
          <div className="card">
            <div className="card-header">
              <label className="color-primary">Ideafix</label>
              <div className="card-delete">&times;</div>
            </div>
            <hr className="border-dashed" />
            <div className="card-body">Empresa</div>
          </div>
        </div>
      </div>
    </div>
  );
};
